import Viewport from "./Components/Viewport";
export default class Lucius {
    Viewport : Viewport;
    constructor() {
        this.Init();
    }
    Init() {
        this.Viewport = new Viewport();

        window.addEventListener('load', ()=>{
            this.Viewport.Resize();
        }, false);

        window.addEventListener('resize', ()=>{
            this.Viewport.Resize()
        }, false);

        this.LockViewport();

        let color : string = '#59abde';

        this.Viewport.SetBackgroundColor(color);
        this.ShowSplashScreen(null,'dark');
    }

    ShowSplashScreen(imageURL?,theme : string = 'dark') {
        const LuciusWhiteLogo = "data:image/png;base64,"
        const LuciusBlackLogo = "data:image/png;base64,"

        let splashLogo = new Image();

        splashLogo.src = theme === 'dark' ? LuciusWhiteLogo : LuciusBlackLogo;

        setTimeout(()=>{
            this.Viewport.Instance.drawImage(splashLogo, (this.Viewport.Instance.canvas.width/2)-(splashLogo.width/2)/1.5, this.Viewport.Instance.canvas.height/2,
                splashLogo.width/1.5,
                splashLogo.height/1.5);
        }, 25);
    }

    LockViewport() {
        document.addEventListener('keydown', (e)=>{
            if(e.ctrlKey && (
                e.keyCode === "61" || //ctrl +
                e.keyCode === "107" || //ctrl +
                e.keyCode === "173" || //ctrl -
                e.keyCode === "109" || //ctrl -
                e.keyCode === "187" || //ctrl +
                e.keyCode === "189" //ctrl -
            )) {
                e.preventDefault();
            }
        });
        document.addEventListener('wheel', (e)=>{
            e.preventDefault();
        },{passive:false});
    }
}